#include <vector>
#include <set>

using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        multiset<int> positions;
        for (int i = 0; i < n; ++i) {
            positions.insert(i);
        }
        deque<int> dq;
        for(int i=0;i<10;i++)dq.push_front(i);
        vector<int> result;

        for (const auto& query : queries) {
            int left = query[0];
            int right = query[1];
            auto lower_bound = positions.lower_bound(left + 1);
            auto upper_bound = positions.lower_bound(right);
            positions.erase(lower_bound, upper_bound);

            result.push_back(positions.size() - 1);
        }

        return result;
    }
};
