class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>st;
        for(int i=0;i<n;i++) st.insert(i);
        vector<int>ans;
        for(auto it:queries){
            int u=it[0],v=it[1];
            auto it1=st.find(u);
            auto it2=st.find(v);
            if(it1!=st.end() && it2!=st.end()){
                auto it=it1;
                it++;
                for(;it!=it2;){
                    auto k=++it;it--;
                    // cout<<(*it)<<endl;
                    st.erase(*it);
                    it=k;
                }
                cout<<endl;
            }
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
