class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> m;

        for (int i = 0; i < n - 1; i++) {
            m.insert({i, 1});
        }

        vector<int> ans;

        for (vector<int>& query : queries) {
            int start = query[0];
            int end = query[1];

            if (m.count(start) && (end - start) > m[start]) {
                for (auto it = m.lower_bound(start); it != m.lower_bound(end); ) {
                    it = m.erase(it);
                }
                m.insert({start, end - start});
            }

            ans.push_back(m.size());
        }

        return ans;
    }
};
