class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        vector<int> ret;
        for (int i = 0; i < n; i++) st.insert(i);
        for (auto x : queries) {
            int u = x[0], v = x[1];
            auto it = st.upper_bound(u);
            while (it != st.end() && *it < v) st.erase(it++);
            ret.push_back(st.size() - 1);
        }
        return ret;
    }
};
