class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> data;
        for(int i=0;i<n;i++)
        data.insert(i);
        vector<int> next(n);

        for(int i=0;i<n-1;i++)
        {
            next[i]=i+1;
        }
      vector<int> ans;
        for(auto i:queries)
        {   
            data.erase(data.upper_bound(i[0]),data.lower_bound(i[1])); 

            ans.push_back(data.size()-1);
        }
        return ans;
    }
};
