class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int m = queries.size();
        vector<int> res(m,n-1);
        set<int> s;
        for(int i=0; i<n-1; ++i) s.insert(i);
        for(int i=0; i<m; ++i){
            int u = queries[i][0], v = queries[i][1];
            auto itr = s.upper_bound(u);
            while(itr != s.end() && (*itr < v)){
                s.erase(itr);
                itr = s.upper_bound(u);
            }
            res[i] = s.size();
        }
        return res;
    }
};
