class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        paths = {}
        S = set(range(n))
        for i in range(n-1):
            paths[i] = i + 1

        steps = n - 1
        L = []
        for u, v in queries:
            if u in S:
                if paths[u] < v:
                    curr = u
                    while paths[curr] != v:
                        curr = paths[curr]
                        S.remove(curr)
                        steps -= 1
                    paths[u] = v

            L.append(steps)

        return L
