class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qu) {
         int min=n-1;
         vector<int>ans(n,0);
         for(int i=1;i<n;i++){
            ans[i]=ans[i-1]+1;
         }
         set<int>pos;
         for(int i=0;i<n;i++){
            pos.insert(i);
         }
         vector<int>res;
         for(auto it:qu){
            int l=it[0];
            int r=it[1];
            auto i=pos.lower_bound(l+1);
            auto j=pos.lower_bound(r);
            pos.erase(i,j);
            res.push_back(pos.size()-1);
         }
         return res;
    }
};
