class Solution {
public:
    // No queries[i][0] < queries[j][0] < queries[i][1] < queries[j][1]
    // shorter path included in greater path is useless
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> nodes;
        for(int i = 0; i < n; i++)
            nodes.insert(i);
        for(auto& q: queries){
            auto it = nodes.lower_bound(q[0]);
            for(++it; it != nodes.end() && (*it) < q[1]; ){
                nodes.erase(it++);
            }
            ans.push_back(int(nodes.size()) - 1);
        }
        return ans;
    }
};
