using vi = vector<int>;
#define INIT_VEC(n, v) for (int i = 1; i < (n); ++i) (v)[i] = (v)[i - 1] + 1
#define INS_SET(s, n) for (int i = 0; i < (n); ++i) (s).insert(i)
#define ES(s, l, r) (s).erase((s).lower_bound(l), (s).lower_bound(r))
#define PB(v, e) (v).push_back(e)
#define FORI(n, i) for (int i = 0; i < (n); ++i)
#define FORV(vec, e) for (const auto& e : (vec))
class Solution {
public:
    vi shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
    set<int> p;
        INS_SET(p, n);
        vi r;
         vi d(n, 0); INIT_VEC(n, d);

//         FORV(q, qi) {
//             int l = qi[0], rt = qi[1];
//             ES(p, l + 1, rt);
//             PB(r, p.size() - 1);
//         }

         FORV(q, qi) {
            int l = qi[0], rt = qi[1];
            ES(p, l + 1, rt);
            PB(r, p.size() - 1);
        }

        return r;
    }
};
