class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        Set<Integer> se = new HashSet<>();
        int[] dp=new int[n];
        for(int i=0;i<n;i++){
            se.add(i);
            dp[i]=i+1;
        }
        int t=queries.length;
        int[] ans=new int[t];
        for(int i=0;i<t;i++){
            int x=queries[i][0];
            int y=queries[i][1];
            // System.out.println(x+" "+y);
            int j=x;
            while(dp[j]<y){
                int temp=dp[j];
                if(temp<y){
                    se.remove(temp);
                    // System.out.println(temp);
                }
                dp[j]=Math.max(temp,y);
                j=temp;
            }
            // System.out.println("----------------------------------");
            ans[i]=se.size()-1;
        }
        return(ans);
    }
}
