from sortedcontainers import SortedList


class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        sl = SortedList([x for x in range(n)])
        cnt = set([x for x in range(n)])
        ans = []
        for x, y in queries:
            if x in cnt and y in cnt:
                l = sl.bisect_left(x)
                r = sl.bisect_left(y)
                c = set()
                for i in range(l + 1, r):
                    c.add(sl[i])
                for v in c:
                    cnt.remove(v)
                    sl.discard(v)
            ans.append(len(cnt) - 1)
        return ans
