class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {

        set<int>s;
        vector<int>ans;
        for(int i = 0;i<n-1;i++)      s.insert(i);

        for(int i = 0;i<q.size();i++){

            auto pos = s.find(q[i][0]);

            if(pos == s.end()){
                ans.push_back((int)s.size());
                continue;
            }

            pos++;



            vector<int>r;
            for(auto it = pos; it != s.end();it++){
                if((*it) < q[i][1])       r.push_back((*it));
                else        break;
            }
            for(auto it: r){
                s.erase(it);
            }
            ans.push_back((int)s.size());
        }
        return ans;

    }
};
