class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
       vector<int> distance(n,0);
        for(int i=1;i<n;++i)
        {
            distance[i]=distance[i-1]+1;
        }
        set<int> st;
        for(int i=0;i<n;++i)
        {
            st.insert(i);
        }
        vector<int> ans;
        for(int i=0;i<queries.size();++i)
        {
            int node1=queries[i][0];
            int node2=queries[i][1];
            auto path1=st.lower_bound(node1+1);
            auto path2=st.lower_bound(node2);
            st.erase(path1,path2);
            int len=st.size();
            ans.push_back(len-1);
        }
        return ans;

    }
};
