class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ret;
        set<int>next;

        for(int i=0;i<n;i++)
        next.insert(i);

        for(auto x:queries)
        {
            auto it=next.upper_bound(x[0]);
            while(*it<x[1]&&*it>x[0])
            {
                next.erase(it);
                it=next.upper_bound(x[0]);
            }
            ret.push_back(next.size()-1);
        }

        return ret;
    }
};
