class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>st;
        for(int i=0;i<n;i++) st.insert(i);
        vector<int>ans;
        for(auto it: queries){
            int u=it[0],v=it[1];
            if(u>v){
                int N=st.size();
                ans.push_back(N-1);
                continue;
            }
            while(1){
                auto it=st.upper_bound(u);
                int val=(*it);
                if(val>=v) break;
                st.erase(it);
            }
            int N=st.size();
            ans.push_back(N-1);
        }
        return ans;
    }
};
