class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        vector<int> ans(queries.size());
        for(int i=0;i<n;i++)
            st.insert(i);
        int i=0;
        for(auto x:queries)
        {
            auto it=st.upper_bound(x[0]);
            while(*it<x[1])
            {
                st.erase(it);
                it=st.upper_bound(x[0]);
            }
            // cout<<st.size()<<endl;
            ans[i]=st.size()-1;
            i++;
        }
        return ans;
    }
};
