class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:

        roads = {}
        for i in range(n - 1):
            roads[i] = i + 1
        res = []
        for origin, target in queries:
            i = origin
            while i in roads and i < roads[i] <= target:
                next_i = roads[i]
                del roads[i]
                i = next_i
                if origin not in roads:
                    roads[origin] = target
                #print(i, roads)
            res.append(len(roads))
        return res
