import sortedcontainers

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        m = len(queries)
        ans = [0] * m
        st = sortedcontainers.SortedList([i for i in range(n)])
        for i, (u, v) in enumerate(queries):
            p1 = st.bisect_left(u)
            if p1 >= len(st) or st[p1] != u:
                ans[i] = ans[i - 1]
                continue
            p2 = st.bisect_left(v)
            if p2 >= len(st) or st[p2] != v:
                ans[i] = ans[i - 1]
                continue
            for j in range(p2 - 1, p1, -1):
                del st[j]
            ans[i] = len(st) - 1
        return ans
