class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dis(n, 0);

        for (int i = 1; i < n; ++i) {
            dis[i] = dis[i - 1] + 1;
        }

        set<int> pos;

        for (int i = 0; i < n; ++i) {
            pos.insert(i);
        }

        vector<int> res;

        for (const auto& query : queries) {
            int left = query[0], right = query[1];

            auto upper_bound_it = pos.lower_bound(right);
            auto lower_bound_it = pos.lower_bound(left + 1);

            pos.erase(lower_bound_it, upper_bound_it);

            res.push_back(pos.size() - 1);
        }

        return res;
    }
};
