type node struct {
    val int
    next *node
}

func shortestDistanceAfterQueries(n int, queries [][]int) []int {
    list := &node{}
    cur := list
    m := make(map[int]*node)

    for i := 0; i < n; i++ {
        cur.next = &node{val: i}
        cur = cur.next
        m[i] = cur
    }

    // for c := list.next; c != nil; c = c.next {
    //     fmt.Println(c.val, m[c.val].val)
    // }

    l := n-1
    ans := make([]int, len(queries))
    for i, q := range queries {
        u, v := q[0], q[1]
        start := m[u]
        end := m[v]

        if start == nil || end == nil {
            ans[i] = l
            continue
        }

        // fmt.Println(start.val, end.val)

        for start.next.val < v {
            delete(m, start.next.val)
            start.next = start.next.next
            l -= 1 
        }   

        ans[i] = l
    }

    return ans
}
