class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> ranges;

        for (int i = 0; i < n - 1; i++) {
            ranges[i] = i + 1;
        }

        vector<int> result;
        for (auto & query: queries) {
            auto left = query[0];
            if (ranges.find(left) == ranges.end()) {
                result.push_back(ranges.size());
                continue;
            }
            auto right = query[1];
            auto existing = ranges.lower_bound(left);
            while (existing->second < right) {
                existing->second = next(existing)->second;
                ranges.erase(next(existing));
            }
            result.push_back(ranges.size());
        }
        return result;
    }
};
