class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> se;
        for(int i = 0; i < n; i++) se.insert(i);
        vector<int> ans;
        for(auto v : queries) {
            int a = v[0], b = v[1];
            auto it = se.upper_bound(a);
            while(it != se.end() && *it < b) {
                it = se.erase(it);
            }
            ans.push_back(se.size() - 1);
        }
        return ans;
    }
};
