typedef pair<int, int> pa;
class Solution {
public:
  vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
    set<pa> s;
    for (int i = 0; i < n - 1; i++) {
      s.insert({ i,i + 1 });
    }
    vector<int>ret(queries.size());
    for (int q = 0; q < queries.size(); q++) {
      int l = queries[q][0];
      int r = queries[q][1];
      auto it = s.lower_bound({ l, 0 });
      int cnt = 0;
      while (it != s.end() && it->second <= r) {
        // cout << q << " <<" << it->first << " " << it->second << endl;
        auto nxt = it;
        nxt++;
        s.erase(it);
        it = nxt;
        cnt++;
      }
      if (cnt > 0) {
        s.insert({ l, r });
      }

      ret[q] = s.size();
      // cout << q << " " << ret[q] << endl;
    }
    return ret;
  }
};
