/**
 * @param {number} n
 * @param {number[][]} queries
 * @return {number[]}
 */
var shortestDistanceAfterQueries = function(n, queries) {
    var distance  = n - 1;

    var dropped = Array(n).fill(false);

    var used = new Map();

    var next = Array.from({length: n}, (_, index) => index + 1);

    var u;
    var v;

    var nextNode;

    var output = [];

    for(let i = 0; i < queries.length; i++) {
        u = queries[i][0];
        v = queries[i][1];

        if( dropped[u] == true ) {
            output.push(distance);
            continue;
        }

        var val = used.get(u);

        if(val != undefined && v <= val) {
            output.push(distance);
            continue;
        }

        nextNode = next[u];

        while( nextNode != v && nextNode < n) {
            dropped[nextNode] = true;
            nextNode = next[nextNode];
            next[u] = nextNode;
            distance--;
        }

        used.set(u, v);
        output.push(distance);
    }

    return output;
};
