class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>s;
        for(int i=0;i<n;i++){
            s.insert(i);
        }
        vector<int>an;
        for(auto &e:queries){
            auto x=s.lower_bound(e[0]+1);
            auto y=s.lower_bound(e[1]);
            s.erase(x,y);
            an.push_back(s.size()-1);
        }
        return an;
    }
};
