class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
          vector<int> ans;
        set<int> dist;
        for (int i=0;i<n;++i) {
            dist.insert(i);
        }

        for (const auto q : queries) {
            int u = q[0], v=q[1];
            auto ub = dist.lower_bound(v);
            auto lb = dist.lower_bound(u + 1);
            dist.erase(lb, ub);
            int path=dist.size() - 1;
            ans.push_back(path);
        }
        return ans;
    }
};
