class Solution {
public:
  vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
    set<int> s;
    for(int i=1;i<n;i++)
      s.emplace(i);
    vector<int> w;
    for(auto i:q){
      for(auto it=s.upper_bound(i[0]);it!=s.end()&&*it<i[1];it=s.erase(it));
      w.emplace_back(s.size());
    }
    return w;
  }
};
