from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        intervals = SortedList()
        ans, d = [], n - 1
        for l, r in queries:
            idx = intervals.bisect_left((l, r))
            intv2del = []
            for i in range(max(0, idx - 1), len(intervals)):
                u2, v2 = intervals[i]
                if u2 >= r:
                    break
                if max(l, u2) < min(r, v2):
                    intv2del.append((u2, v2))
                    l, r = min(l, u2), max(r, v2)
            for u, v in intv2del:
                d += (v - u - 1)
                intervals.discard((u, v))
            d -= r - l - 1
            intervals.add((l, r))
            ans.append(d)
        return ans
