class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<vector<int>> next(n);
        set<pair<int, int>> st;
        for(int i = 0; i < n-1; i++){
            next[i].push_back(i+1);
            st.insert({i, i+1});
        }

        vector<int> ans;
        int x = n-1;
        for(auto &q : queries){
            int y = q[1] - q[0] - 1;
            auto it = st.lower_bound({q[0],q[0]});
            if(it != st.end()){
                if(it->first == q[0] && it->second > q[1]){
                    ans.push_back(x);
                    continue;
                }
            }
            if(it != st.begin()){
                it--;
                if(it->second >= q[1]){
                    ans.push_back(x);
                    continue;
                }
                it++;
            }

            while(it != st.end() && it->second <= q[1]){
                y -= it->second - it->first - 1;
                it = st.erase(it);
            }

            st.insert({q[0],q[1]});
            x -= y;
            ans.push_back(x);
        }
        return ans;
    }
};
