class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i = 0; i < n; i++) {
            s.insert(i);
        }
        vector<int> ans;
        for(vector<int> q: queries) {
            auto it = s.upper_bound(q[0]);
            auto it1 = s.lower_bound(q[1]);
            s.erase(it, it1);
            ans.push_back(s.size() - 1);
        }
        return ans;
    }
};
