class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int, int>> ran;
        for (int i = 0; i < n - 1; i++) {
            ran.emplace(i, i + 1);
        }

        vector<int> ans;
        for (auto q : queries) {
            int x = q[0], y = q[1];
            auto it = --ran.upper_bound({x + 1, -1});
            auto [a, b] = *it;
            if (b < y) {
                for (; it != ran.end() && it->second <= y;) {
                    it = ran.erase(it);
                }
                ran.emplace(x, y);
            }
            ans.push_back(ran.size());
        }

        return ans;
    }
};
