class Solution {
public:
    std::vector<int> shortestDistanceAfterQueries(int n, std::vector<std::vector<int>>& queries) {

        std::set<int> nodes;
        std::vector<int> result(queries.size());
        int cost = n;

        for(int i = 0; i < n; i++){
            nodes.insert(i);
        }

        for(int i = 0; i < queries.size(); i++){


            auto it = nodes.upper_bound(queries[i][0]);


            while(*it < queries[i][1]){                
                auto next = it;
                next++;
               // std::cout<<"removing "<<*it<<std::endl;
                nodes.erase(it);
                it = next;
                cost--;
            }


            result[i] = cost-1; 
        }

        return result;
    }

    // 0->1->2->4
};
