#include <vector>
#include <set>
using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int m = q.size();
        vector<int> ans(m, 0);
        set<int> dist;

        for (int i = 1; i < n; i++) {
            dist.insert(i);
        }

        int currDist = n - 1;

        for (int i = 0; i < m; i++) {
            int s = q[i][0], e = q[i][1];
            auto it = dist.lower_bound(s + 1);
            while (it != dist.end() && *it < e) {
                it = dist.erase(it);
                currDist--;
            }
            ans[i] = currDist;
        }

        return ans;
    }
};
