class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = dict()
        remaining = set(range(n))
        for i in range(n):
            if i != n - 1:
                g[i] = i + 1

        def traverse(u: int, v: int):
            to_remove = set()
            cur = u
            while True:
                cur = g[cur]
                if cur != v:
                    to_remove.add(cur)
                else:
                    break

            return to_remove


        dist = n - 1
        dists = []
        for i, j in queries:
            if i in remaining and j in remaining:
                remove = traverse(i, j)
                g[i] = j
                dist -= len(remove)
                for x in remove:
                    remaining.remove(x)

            dists.append(dist)

        return dists
