class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set <int> st;
        for(int i = 0; i<n; i++) st.insert(i);
        vector <int> ans;
        for(auto &it: q){
            auto start = st.lower_bound(it[0]), end = st.lower_bound(it[1]);
            if(*start!=it[0] || *end!=it[1]) ans.emplace_back(st.size()-1);
            else{
                start++;
                st.erase(start, end);
                ans.emplace_back(st.size()-1);
            }
        }
        return ans;
    }
};
