class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dists;
        int dist = n - 1;
        vector<int> nodes(n, -1);

        for (int node = 0; node + 1 != n; ++node) nodes[node] = node + 1;

        for (auto &query : queries) {
            for (int node = nodes[query[0]]; node != query[1] && node != -1; ) {
                int next = nodes[node];

                if (nodes[node] != -1) {
                    --dist;
                    nodes[node] = -1;
                }

                node = next;
            }

            nodes[query[0]] = max(nodes[query[0]], query[1]);

            dists.push_back(dist);
        }

        return dists;
    }
};
