class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int[] ans = new int[queries.length];
        int idx = 0;
        int[] next = new int[n];

        Set<Integer> anc = new HashSet<>();
        for (int i = 0; i < n-1; i++) next[i] = i+1;
        for (int i = 0; i < n; i++) anc.add(i);

        for (int[] q : queries){
            int u = q[0];
            int v = q[1];
            if (anc.contains(u) && anc.contains(v)){
                int node = next[u];
                while (node != v){
                    anc.remove(node);
                    node = next[node];
                }
                next[u] = v;
            }
            ans[idx++] = anc.size()-1;
        }
        return ans;
    }
}
