using ll = long long;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9+7;

        vector<ll> dp(nums[0] + 1, 1);
        for (int i = 1; i < dp.size(); i++) dp[i] += dp[i-1], dp[i] %= MOD;
        for (int i = 1; i < nums.size(); i++) {
            vector<ll> cdp(nums[i] + 1, 0);
            for (int x = 0; x <= nums[i]; x++) {
                // y <= x
                // (nums[i-1] - y) >= (nums[i] - x)
                    // x + nums[i-1] - nums[i] >= y
                int bound = min({x, x + nums[i-1] - nums[i], nums[i-1]});
                if (bound < 0) continue;
                cdp[x] += dp[bound];
                if (cdp[x] >= MOD) cdp[x] -= MOD;
            }

            for (int i = 1; i < cdp.size(); i++) {
                cdp[i] += cdp[i-1];
                if (cdp[i] >= MOD) cdp[i] -= MOD;
            }

            swap(dp, cdp);
        }

        return dp.back();
    }
};
