class Solution {
public:
    int mo = 1000000007;
    int f[2005][1005];
    int countOfPairs(vector<int>& a) {
        int i, j, k, n = a.size(), zd = 0, res = 0;
        for (auto x : a) zd = max(zd, x);
        for (i = 0; i <= zd; ++i)
            if (i <= a[0]) f[0][i] = 1;
            else f[0][i] = 0;
        for (i = 1; i < n; ++i)
            for (j = 0; j <= zd; ++j){
                f[i][j] = 0;
                if (j > a[i]) continue;
                for (k = 0; k <= j && a[i] - j <= a[i - 1] - k; ++k)
                    f[i][j] = (f[i][j] + f[i - 1][k]) % mo;
            }


        for (j = 0; j <= zd; ++j) res = (res + f[n - 1][j]) % mo;
        return res;
    }
};
