class Solution {
public:
  int countOfPairs(vector<int>& nums) {

    int n = nums.size(), ans = 0, M = 1e9 + 7;

    vector<vector<long long>> dp(n, vector<long long> (51, -1));

    auto f = [&](int i, int prev, auto && f) -> long long {

      if(i == n) return 1;

      if(dp[i][prev] != -1) return dp[i][prev];

      long long ans = 0;

      for(int j = prev; j <= nums[i]; j++) {

        int a = prev, b = (i ? nums[i - 1] - prev: 50);

        int cura = j, curb = nums[i] - j;

        if(cura >= a && curb <= b) ans = (ans + f(i + 1, cura, f)) % M;

      }

      return dp[i][prev] = ans;

    };

    return f(0, 0, f);

  }
};
