class Solution {
public:
    const int mod = 1e9+7;

    int fun(vector<int> &nums, int ind, int last1, vector<vector<int>> &dp){
        if(ind==nums.size())
            return 1;
        if(dp[ind][last1]!=-1)
            return dp[ind][last1];

        long long res = 0;
        int last2 = 51;
        if(ind-1>=0)
            last2 = nums[ind-1]-last1;

        for(int l1=last1; l1<=nums[ind]; l1++){
            int l2 = nums[ind]-l1;
            if(l2>last2) continue;
            res += fun(nums, ind+1, l1, dp);
            res %= mod;
        }

        return dp[ind][last1] = res;
    }

    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size(), vector<int>(*max_element(nums.begin(), nums.end())+1, -1));
        return fun(nums, 0, 0, dp);
    }
};
