class Solution {
public:
    const int mod = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = (int) nums.size();
        vector<vector<int>> dp(n, vector<int> (55));
        for (int i = 0; i <= nums[0]; i++) dp[0][i] = 1;
        for (int i = 1; i < n; i++) {
            for (int k = 0; k <= nums[i]; k++) {
                for (int j = 0; j <= nums[i]; j++) {
                    if (k >= j && dp[i - 1][j]) {
                        int x = nums[i - 1] - j;
                        int y = nums[i] - k;
                        if (x >= y) {
                            dp[i][k] = (dp[i][k] + dp[i - 1][j]) % mod;
                        }
                    }
                }
            }
        }

        int ans = 0;
        for (int i = 0; i < 55; i++) ans = (ans + dp[n - 1][i]) % mod;

        return ans;

    }
};
