#include <vector>
#include <algorithm>

class Solution {
public:
    int countOfPairs(std::vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();

        std::vector<std::vector<std::vector<long long>>> dp(n + 1, std::vector<std::vector<long long>>(51, std::vector<long long>(51, -1)));

        auto count = [&](int i, int last1, int last2, auto&& count) -> long long {
            if (i == n) return 1;

            if (dp[i][last1][last2] != -1) return dp[i][last1][last2];

            long long result = 0;
            for (int x1 = last1; x1 <= nums[i]; ++x1) {
                int x2 = nums[i] - x1;
                if (x2 <= last2) {
                    result = (result + count(i + 1, x1, x2, count)) % MOD;
                }
            }

            dp[i][last1][last2] = result;
            return result;
        };

        return count(0, 0, 50, count); 
    }
};
