int dp[2002][52];
const int mod = 1e9 + 7;
class Solution {
    long long fun(vector<int>& nums, int i, int prev) {
        if (i == nums.size()) return 1;
        if (dp[i][prev] != -1) return dp[i][prev];

        long long ans = 0;
        if (i == 0) {
            for (int j = 0; j <= nums[0]; j++) {
                ans += fun(nums, i + 1, j);
                ans %= mod;
            }
        }
        else {
            int prev2 = nums[i - 1] - prev;
            for (int j = prev; j <= nums[i]; j++) {
                int now = nums[i] - j;
                if (now <= prev2) {
                    ans += fun(nums, i + 1, j);
                    ans %= mod;
                }
            }
        }
        return dp[i][prev] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= 50; j++) {
                dp[i][j] = -1;
            }
        }
        return fun(nums, 0, 0) % mod;
    }
};
