class Solution {
public:

     const int MOD = 1e9 + 7;

    int func(int ind, int &n, int preva, int prevb, vector<vector<vector<int>>>& dp,vector<int>& nums) {
        if (ind >= n) {
            return 1;
        }


        if (dp[ind][preva][prevb] != -1) {
            return dp[ind][preva][prevb];
        }

        int ans = 0;
        for (int i = 0; i <= nums[ind]; i++) {
            int a = i;
            int b = nums[ind] - i;
            if (a >= preva && b <= prevb) {
                ans = (ans + func(ind + 1, n, a, b, dp, nums)) % MOD;
            }
        }

        return dp[ind][preva][prevb] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return func(0, n, 0, 50, dp, nums);
    }
};
