class Solution {
public:
const int mod=1e9+7;
  int solve(vector<int> & nums, int idx, int prev,vector<vector<int>>& dp){
     if(nums.size()==idx)
      return 1;
     int ans=0;
     if(dp[idx][prev+1]!=-1)
      return dp[idx][prev+1];
     if(prev==-1){
        for(int i=0;i<=nums[idx];i++){
            ans=(ans+solve(nums,idx+1,i,dp))%mod;
        }
     }else{
        int prev2=nums[idx-1]-prev;
        for(int i=0;i<=prev && nums[idx]-i>=prev2;i++){
            ans=(ans+solve(nums,idx+1,i,dp))%mod;
        }
     }
     return dp[idx][prev+1]=ans%mod;
  }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int maxi=*max_element(nums.begin(),nums.end());
        vector<vector<int>> dp(n+1,vector<int>(maxi+2,-1));
        int ans=solve(nums,0,-1,dp);
        return ans;
    }
};
