class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 +7
        @cache
        def dfs(i:int, pre1:int, pre2: int) -> int:
            if i==n:
                return 1
            ret = 0
            for n1 in range(pre1, nums[i]+1):
                n2 = nums[i] - n1
                if n1 >= pre1 and n2 <= pre2:
                    ret += dfs(i+1, n1, n2)
            return ret % MOD

        val = dfs(0, 0, math.inf)
        return val
