class Solution {
public:
    int anil(int ind, int n, int prev1, int prev2, vector<int>& nums, vector<vector<vector<int>>>& dp) {
        if (ind == n) return 1;
        if (dp[ind][prev1 + 1][prev2] != -1) return dp[ind][prev1 + 1][prev2];

        int ans = 0;
        int val = nums[ind];

        for (int i = 0; i <= val; i++) {
            if (i >= prev1 && (val - i) <= prev2) {
                ans += anil(ind + 1, n, i, val - i, nums, dp);
                ans %= 1000000007; 
            }
        }

        return dp[ind][prev1 + 1][prev2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(52, vector<int>(51, -1)));
        return anil(0, n, -1, 50, nums, dp);
    }
};
