class Solution(object):
    def countOfPairs(self, nums):
        mod = 10**9 + 7
        n = len(nums)
        mx = max(nums)
        dp = [[0]*(mx+1) for _ in range(n)]
        for i in range(nums[0]+1):
            dp[0][i] = 1

        for i in range(1, n):
            num = nums[i]
            prev = nums[i-1]
            for j in range(num + 1):
                for k in range(j+1):
                    if prev - k >= num - j:
                        dp[i][j] += dp[i-1][k]
            dp[i][j] %= mod

        return sum(dp[-1])%mod


        """
        :type nums: List[int]
        :rtype: int
        """
